package com.zaid.note.base;/** * ==判断的是对象的地址而非值,equals判断的是对象的值 */public class EqualsString {    public static void main(String[] args) {        String s1 = "a";        String s2 = "a";        String s3 = new String("a");        String s4 = new String("a");        String s5 = s1;        String s6 = s3;        System.out.println(s1 == s2);        System.out.println(s1.equals(s2));        System.out.println(s3 == s4);        System.out.println(s3.equals(s4));        System.out.println(s1 == s3);        System.out.println(s1.equals(s3));        System.out.println(s5 == s6);        System.out.println(s5.equals(s6));    }}